import React, { useState } from 'react'
import { useSpring, animated as a } from 'react-spring'
import { Container, Row } from "react-bootstrap"
import '../receipt.css';
import Receipt from "../components/receipt";
import Receipt2 from "../components/receipt2";

export default () => {

  const [flipped, set] = useState(false)
  const { transform, opacity } = useSpring({
    opacity: flipped ? 1 : 0,
    transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
    config: { mass: 5, tension: 500, friction: 80 }
  })

  return (
    <div className="component tenth-component">
      <Container className="center-block" style={{ alignSelf: 'center', }}>
        <Row>
          <h2>ค่าใช้จ่ายขั้นต่ำ social distancing (wfh/ หยุดงาน?) จนถึงวันที่ 30 เมษา ตามประกาศของรัฐบาล
            อาหาร
            ไข่ไก่ 6 ฟอง 40 บาท
            ค่าเดินทาง
            ค่าที่อยู่
            นี่ยังไม่รวมค่าแอลกอฮอลล้างมือ / หน้ากากอนามัย / ค่าไฟจากการฟังแถลงที่ไม่รู้เรื่อง / ค่าประกันโควิด ...
          </h2>
        </Row>
        <Row>
          <div onClick={() => set(state => !state)}>
            <animated.Receipt style={{ opacity: opacity.interpolate(o => 1 - o), transform }} />
            <animated.Receipt2 style={{ opacity, transform: transform.interpolate(t => `${t} rotateX(180deg)`) }} />
          </div>
        </Row>
      </Container>
    </div>
  );
};
