import React from "react";
import TextLoop from "react-text-loop";
import { makeStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';

// import '../styles/main.scss'
import { Container, Row, Col, Button } from "react-bootstrap"
import { color } from "@material-ui/system";

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  formControl: {
    margin: theme.spacing(3),
    fontFamily: 'Pridi',
    fontSize: '30px'
  },
}));

export default () => {

  const classes = useStyles();
  const [state, setState] = React.useState({
    gilad: true,
    jason: false,
    antoine: false,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { gilad, jason, antoine } = state;
  const error = [gilad, jason, antoine].filter((v) => v).length !== 2;

  return (
    <div className="component second-component">
      <Container fluid className="center-block" style={{ alignSelf: 'center', }}>
        <Row>
          < Col style={{ marginBottom: '50px' }}>
            <h3>รายการสินค้าเพื่อสุขภาพและความปลอดภัยสำหรับตัวคุณ</h3>
          </Col >
        </Row>
        <Row>
          <Col >
            <FormControl style={{ marginLeft: '50px' }}>
              <FormGroup>
                <FormControlLabel
                  control={<Checkbox checked={gilad} onChange={handleChange} name="gilad" />}
                  label={<p className='text-info'>หน้ากากอนามัย</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={jason} onChange={handleChange} name="jason" />}
                  label={<p className='text-info'>เจลล้างมือ</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>แอลกอฮอล์แบบขวด</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ไข่ไก่</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>สบู่ฆ่าเชื้อโรค</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>รถทัวร์กลับต่างจังหวัด</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ค่าอาหารดิลิเวอร์รี่</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ค่ารักษาพยาบาล</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ค่าประกันCovid-19</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ค่าไฟ</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                  label={<p className='text-info'>ค่าอินเตอร์เน็ต</p>}
                />
                <FormControlLabel
                  control={<Checkbox checked={antoine} onChange={handleChange} name="fixed" disabled />}
                  label="ค่าครองชีพในกรุงเทพ"
                />
              </FormGroup>
            </FormControl>
          </Col>
        </Row>
        <span><p className='text-info' style={{ marginLeft: '50px' }}>รวมทั้งสิ้น</p><p className='text-info' style={{ marginLeft: '50px' }}>บาท</p></span>
      </Container>
    </div >
  );
};
