import React from "react";
import posed from 'react-pose';
import SplitText from 'react-pose-text';
import { Container } from "react-bootstrap"

export default () => {

  const charPoses = {
    exit: { x: 0, opacity: 10000 },
    enter: {
      x: -100,
      opacity: 5,
      transition: ({ wordIndex }) => ({
        type: 'tween',
        delay: 1000,
      })
    }
  };
  const charPoses2 = {
    exit: { x: 0, opacity: 10000 },
    enter: {
      x: 100,
      opacity: 5,
      transition: ({ wordIndex }) => ({
        type: 'tween',
        delay: 1000,
      })
    }
  };
  return (
    <div className="component first-component">
      <Container className="center-block" style={{ alignSelf: 'center', display: 'flex', justifyContent: 'center', }}>
        <div style={{ weight: '300px' }}>
          <SplitText
            initialPose="exit"
            pose="enter"
            charPoses={charPoses}
            style={{
              color: '#cfff3c',
              fontSize: '100px',
              fontFamily: 'Pridi',
              fontWeight: '100px',
              weight: '200px',
              display: 'flex'
            }}>
            Social
      </SplitText>
        </div>
        <SplitText
          initialPose="exit"
          pose="enter"
          charPoses={charPoses2}
          style={{
            color: '#cfff3c',
            fontSize: '100px',
            fontFamily: 'Pridi',
            fontWeight: '100px',
            weight: '200px'
          }}>
          Distancing
      </SplitText>
        {/* <h1 style={{ color: '#cfff3c', fontWeight: '800', }}>SOCIAL DISTANCING</h1> */}
      </Container>
    </div>
  );
};
