import React from "react";
import TextLoop from "react-text-loop";
import { makeStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';

// import '../styles/main.scss'
import { Container, Row, Col, Button } from "react-bootstrap"
import { color } from "@material-ui/system";

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  formControl: {
    margin: theme.spacing(3),
    fontFamily: 'Pridi',
    fontSize: '30px'
  },
}));

export default () => {

  const classes = useStyles();
  const [state, setState] = React.useState({
    gilad: true,
    jason: false,
    antoine: false,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { gilad, jason, antoine } = state;
  const error = [gilad, jason, antoine].filter((v) => v).length !== 2;

  return (
    <div className="component second-component">
      <Container className="center-block" style={{ alignSelf: 'center', }}>
        < Col style={{ marginBottom: '50px' }}>
          <h3>รายการสินค้าเพื่อสุขภาพและความปลอดภัยสำหรับตัวคุณ</h3>
        </Col >
        <Col >
          <FormControl>
            <FormGroup>
              <FormControlLabel
                control={<Checkbox style={{ width: '30px' }} checked={gilad} onChange={handleChange} name="gilad" />}
                label="Gilad Gray"
              />
              <FormControlLabel
                control={<Checkbox checked={jason} onChange={handleChange} name="jason" />}
                label="Jason Killian"
              />
              <FormControlLabel
                control={<Checkbox checked={antoine} onChange={handleChange} name="antoine" />}
                label="Antoine Llorca"
              />
              <FormControlLabel
                control={<Checkbox checked={antoine} onChange={handleChange} name="fixed" disabled />}
                label="ค่าครองชีพในกรุงเทพ"
              />
            </FormGroup>
          </FormControl>
        </Col>
        <h3>รายการสินค้าเพื่อสุขภาพและความปลอดภัยสำหรับตัวคุณ</h3>
      </Container>
    </div >
  );
};
