import React from "react";
import posed from 'react-pose';
import SplitText from 'react-pose-text';
import { Container } from "react-bootstrap"

export default () => {

  const charPoses = {
    exit: { x: 0, opacity: 500000 },
    enter: {
      x: -450,
      opacity: 100,
      transition: ({ wordIndex }) => ({
        type: 'tween',
        delay: 1500,
      })
    }
  };
  const charPoses2 = {
    exit: { x: 0, opacity: 500000 },
    enter: {
      x: 450,
      opacity: 100,
      transition: ({ wordIndex }) => ({
        type: 'tween',
        delay: 1500,
      })
    }
  };
  return (
    <div className="component first-component">
      <Container className="center-block" style={{ alignSelf: 'center', display: 'flex', justifyContent: 'center', }}>
        <div style={{
          justifyContent: 'center',
          alignItems: 'center',
          display: 'flex',
          color: '#cfff3c',
          fontSize: '150px',
          fontFamily: 'Pridi',
          fontWeight: '100px',
        }}>
          <SplitText
            initialPose="exit"
            pose="enter"
            charPoses={charPoses}
          >
            Social
          </SplitText>
          <SplitText
            initialPose="exit"
            pose="enter"
            charPoses={charPoses2}>
            Distancing
      </SplitText>
        </div>
        {/* <h1 style={{ color: '#cfff3c', fontWeight: '800', }}>SOCIAL DISTANCING</h1> */}
      </Container>
    </div>
  );
};
