import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useForm } from "react-hook-form";
import { RHFInput } from 'react-hook-form-input';
import Announce from '../components/announce';
import '../announce.css';
import AnimatedVisibility from '../components/AnimatedVisibility';
// import '../styles/main.scss'
import { Form, FormGroup, FormLabel, FormControl, FormText, Container, Row, Col, Button } from "react-bootstrap"

export default () => {

  function Box({ word }) {
    const color = colors[Math.floor(Math.random() * 9)];
    const [visible, setVisible] = useState(true);

    function hideMe() {
      setVisible(false);
    }

    let style = { borderColor: color, backgroundColor: color };
  }

  return (
    <div className="component sixth-component">
      <Container className="center-block" style={{ alignSelf: 'center', }}>
        <h2>จากปรากดการณ์</h2>
        <div>
          <AnimatedVisibility visible={visible}>
            <div className="box" style={style}>
              <div className="center">{'word'}</div>
              <button className="button bottom-corner" onClick={hideMe}>
                <i className="center far fa-eye fa-lg" />
              </button>
            </div>
          </AnimatedVisibility>
        </div>
        {/* <Announce /> */}
      </Container >
    </div >
  );
};
